import React, { useState } from 'react';
import '../Envelope.css';

const EnvelopeCard = ({ 
  color, 
  emoji, 
  title, 
  onClick 
}) => {
  const [isHovered, setIsHovered] = useState(false);
  
  return (
    <div 
      className={`envelope-container ${color}-envelope`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onClick}
    >
      {/* Envelope Body */}
      <div className="envelope-body">
        {/* Envelope Content */}
        <div className="envelope-content">
          <div className="envelope-emoji">{emoji}</div>
          <div className="envelope-title">{title}</div>
        </div>
      </div>
      
      {/* Envelope Flap */}
      <div className={`envelope-flap ${isHovered ? 'open' : ''}`} />
      
      {/* Envelope Seal (Heart) */}
      <div className={`envelope-seal ${isHovered ? 'hidden' : ''}`}>
        <div className="envelope-heart">❤️</div>
      </div>
    </div>
  );
};

export default EnvelopeCard;
