import React from 'react';
import '../Envelope.css';

const FriendsModal = ({ onClose }) => {
  // Sample data - in a real app, this would come from props or state
  const messages = [
    { id: 1, name: "Alex", message: "Happy birthday! Wishing you all the best!" },
    { id: 2, name: "Taylor", message: "You're amazing! Have a wonderful day!" },
    { id: 3, name: "Jordan", message: "So grateful to have you as a friend!" }
  ];

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2 className="modal-title" style={{ color: '#9c27b0' }}>Messages From Friends</h2>
          <button className="modal-close" onClick={onClose}>&times;</button>
        </div>
        
        <div>
          {messages.map(message => (
            <div key={message.id} className="modal-item purple-theme">
              <p className="modal-item-title" style={{ color: '#9c27b0' }}>{message.name}</p>
              <p className="modal-item-content">{message.message}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FriendsModal;
