import { useEffect, useRef, useState } from "react";
import "../GiftBox.css"; // You can put the extracted CSS here separately

const GiftBox = () => {
  const canvasRef = useRef(null);
  const [isOpened, setIsOpened] = useState(false);

  useEffect(() => {
    if (!isOpened) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const w = (canvas.width = 300);
    const h = (canvas.height = 200);
    let hw = w / 2;
    let hh = h / 2;

    const opts = { /* Same opts from your original */
      strings: ["HAPPY", "BIRTHDAY!"],
      charSize: 16,
      charSpacing: 20,
      lineHeight: 25,
      cx: w / 2,
      cy: h / 2,
      fireworkPrevPoints: 6,
      fireworkBaseLineWidth: 2,
      fireworkAddedLineWidth: 3,
      fireworkSpawnTime: 200,
      fireworkBaseReachTime: 30,
      fireworkAddedReachTime: 30,
      fireworkCircleBaseSize: 10,
      fireworkCircleAddedSize: 5,
      fireworkCircleBaseTime: 30,
      fireworkCircleAddedTime: 30,
      fireworkCircleFadeBaseTime: 10,
      fireworkCircleFadeAddedTime: 5,
      fireworkBaseShards: 3,
      fireworkAddedShards: 2,
      fireworkShardPrevPoints: 3,
      fireworkShardBaseVel: 2,
      fireworkShardAddedVel: 1,
      fireworkShardBaseSize: 1,
      fireworkShardAddedSize: 1,
      gravity: 0.08,
      upFlow: -0.1,
      letterContemplatingWaitTime: 360,
      balloonSpawnTime: 20,
      balloonBaseInflateTime: 10,
      balloonAddedInflateTime: 10,
      balloonBaseSize: 10,
      balloonAddedSize: 5,
      balloonBaseVel: 0.2,
      balloonAddedVel: 0.2,
      balloonBaseRadian: -(Math.PI / 2 - 0.5),
      balloonAddedRadian: -1,
    };

    const Tau = Math.PI * 2;
    const TauQuarter = Tau / 4;

    const calc = {
      totalWidth: opts.charSpacing * Math.max(opts.strings[0].length, opts.strings[1].length),
    };

    const letters = [];

    function Letter(char, x, y) {
      this.char = char;
      this.x = x;
      this.y = y;
      this.dx = -ctx.measureText(char).width / 2;
      this.dy = opts.charSize / 2;
      this.fireworkDy = this.y - hh;

      const hue = (x / calc.totalWidth) * 360;

      this.color = `hsl(${hue},80%,50%)`;
      this.lightAlphaColor = `hsla(${hue},80%,light%,alp)`;
      this.lightColor = `hsl(${hue},80%,light%)`;
      this.alphaColor = `hsla(${hue},80%,50%,alp)`;

      this.reset();
    }

    Letter.prototype.reset = function () {
      this.phase = "firework";
      this.tick = 0;
      this.spawned = false;
      this.spawningTime = (opts.fireworkSpawnTime * Math.random()) | 0;
      this.reachTime = (opts.fireworkBaseReachTime + opts.fireworkAddedReachTime * Math.random()) | 0;
      this.lineWidth = opts.fireworkBaseLineWidth + opts.fireworkAddedLineWidth * Math.random();
      this.prevPoints = [[0, hh, 0]];
    };

    Letter.prototype.step = function () {
      if (this.phase === "firework") {
        if (!this.spawned) {
          ++this.tick;
          if (this.tick >= this.spawningTime) {
            this.tick = 0;
            this.spawned = true;
          }
        } else {
          ++this.tick;

          const linearProportion = this.tick / this.reachTime;
          const armonicProportion = Math.sin(linearProportion * TauQuarter);

          const x = linearProportion * this.x;
          const y = hh + armonicProportion * this.fireworkDy;

          if (this.prevPoints.length > opts.fireworkPrevPoints) this.prevPoints.shift();

          this.prevPoints.push([x, y, linearProportion * this.lineWidth]);

          const lineWidthProportion = 1 / (this.prevPoints.length - 1);

          for (let i = 1; i < this.prevPoints.length; ++i) {
            const point = this.prevPoints[i];
            const point2 = this.prevPoints[i - 1];

            ctx.strokeStyle = this.alphaColor.replace("alp", i / this.prevPoints.length);
            ctx.lineWidth = point[2] * lineWidthProportion * i;
            ctx.beginPath();
            ctx.moveTo(point[0], point[1]);
            ctx.lineTo(point2[0], point2[1]);
            ctx.stroke();
          }

          if (this.tick >= this.reachTime) {
            this.phase = "contemplate";
          }
        }
      } else {
        ctx.fillStyle = this.color;
        ctx.fillText(this.char, this.x + this.dx, this.y + this.dy);
      }
    };

    function initFireworks() {
      ctx.font = `${opts.charSize}px Verdana`;
      letters.length = 0;

      for (let i = 0; i < opts.strings.length; ++i) {
        for (let j = 0; j < opts.strings[i].length; ++j) {
          letters.push(
            new Letter(
              opts.strings[i][j],
              j * opts.charSpacing + opts.charSpacing / 2 - (opts.strings[i].length * opts.charSize) / 2,
              i * opts.lineHeight + opts.lineHeight / 2 - (opts.strings.length * opts.lineHeight) / 2
            )
          );
        }
      }

      anim();
    }

    function anim() {
      requestAnimationFrame(anim);
      ctx.clearRect(0, 0, w, h);
      ctx.translate(hw, hh);

      let done = true;
      for (let l = 0; l < letters.length; ++l) {
        letters[l].step();
        if (letters[l].phase !== "done") done = false;
      }

      ctx.translate(-hw, -hh);

      if (done) {
        for (let l = 0; l < letters.length; ++l) letters[l].reset();
      }
    }

    initFireworks();

    const handleResize = () => {
      hw = w / 2;
      hh = h / 2;
      ctx.font = `${opts.charSize}px Verdana`;
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, [isOpened]);

  return (
    <div className="giftbox-wrapper">
      <div className="giftcontainer">
        <div className="gift">
          <input
            type="checkbox"
            id="gift-click"
            checked={isOpened}
            onChange={(e) => setIsOpened(e.target.checked)}
          />
          <label htmlFor="gift-click" className="click"></label>
        </div>
      </div>
      <div className={`animation-section ${isOpened ? "visible" : "hidden"}`}>
        <canvas ref={canvasRef}></canvas>
      </div>
    </div>
  );
};

export default GiftBox;
