import React, { useState } from 'react';

const EnvelopeCard = ({ 
  color, 
  emoji, 
  title, 
  onClick 
}) => {
  const [isHovered, setIsHovered] = useState(false);
  
  // Define color styles based on the color prop
  const colorStyles = {
    purple: {
      background: '#e0c3fc',
      flap: '#d3a4fb',
      seal: '#9c27b0'
    },
    blue: {
      background: '#bbdefb',
      flap: '#90caf9',
      seal: '#1976d2'
    },
    yellow: {
      background: '#fff9c4',
      flap: '#fff59d',
      seal: '#fbc02d'
    }
  };
  
  // Get the selected color or default to purple
  const selectedColor = colorStyles[color] || colorStyles.purple;
  
  return (
    <div 
      className="cursor-pointer relative w-64 h-48 mx-auto my-8"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onClick}
    >
      {/* Envelope Body */}
      <div 
        className="absolute w-full h-full rounded-md shadow-md overflow-hidden transition-all duration-300"
        style={{ backgroundColor: selectedColor.background }}
      >
        {/* Envelope Content (visible when flap is open) */}
        <div className="absolute inset-0 flex flex-col items-center justify-center p-4">
          <div className="text-4xl mb-2">{emoji}</div>
          <div className="text-lg font-semibold text-center">{title}</div>
        </div>
      </div>
      
      {/* Envelope Flap */}
      <div 
        className="absolute w-full h-1/2 origin-bottom transition-transform duration-500 ease-in-out z-10"
        style={{ 
          backgroundColor: selectedColor.flap,
          clipPath: 'polygon(0 100%, 50% 0, 100% 100%)',
          transform: isHovered ? 'rotateX(-180deg)' : 'rotateX(0deg)',
          transformOrigin: 'top'
        }}
      />
      
      {/* Envelope Seal (Heart) */}
      <div 
        className="absolute w-10 h-10 rounded-full flex items-center justify-center"
        style={{ 
          backgroundColor: selectedColor.seal,
          left: 'calc(50% - 20px)',
          top: 'calc(50% - 20px)',
          zIndex: isHovered ? 0 : 20,
          opacity: isHovered ? 0 : 1,
          transition: 'opacity 0.3s ease-in-out'
        }}
      >
        <div className="text-white text-lg">❤️</div>
      </div>
    </div>
  );
};

export default EnvelopeCard;
