import React from 'react';

const FriendsModal = ({ onClose }) => {
  // Sample data - in a real app, this would come from your state or database
  const messages = [
    { id: 1, name: "Alex", message: "Happy birthday! Wishing you all the best!" },
    { id: 2, name: "Taylor", message: "You're amazing! Have a wonderful day!" },
    { id: 3, name: "Jordan", message: "So grateful to have you as a friend!" }
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-8 max-w-2xl w-full max-h-[80vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-purple-700">Messages From Friends</h2>
          <button 
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700 text-2xl"
          >
            &times;
          </button>
        </div>
        
        <div className="space-y-6">
          {messages.map(message => (
            <div 
              key={message.id} 
              className="bg-purple-50 p-4 rounded-lg border-l-4 border-purple-500"
            >
              <p className="text-purple-800 font-semibold">{message.name}</p>
              <p className="mt-2 text-gray-700">{message.message}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FriendsModal;
