import React, { useState } from 'react';

const EnvelopeCard = ({ 
  color, 
  emoji, 
  title, 
  onClick 
}) => {
  const [isHovered, setIsHovered] = useState(false);
  
  // Define color styles based on the color prop
  const colorStyles = {
    purple: {
      background: '#e0c3fc',
      flap: '#d3a4fb',
      seal: '#9c27b0'
    },
    blue: {
      background: '#bbdefb',
      flap: '#90caf9',
      seal: '#1976d2'
    },
    yellow: {
      background: '#fff9c4',
      flap: '#fff59d',
      seal: '#fbc02d'
    }
  };
  
  // Get the selected color or default to purple
  const selectedColor = colorStyles[color] || colorStyles.purple;
  
  // Style objects to ensure styling works without relying on Tailwind
  const containerStyle = {
    position: 'relative',
    width: '256px',
    height: '192px',
    margin: '32px auto',
    cursor: 'pointer'
  };
  
  const envelopeStyle = {
    position: 'absolute',
    width: '100%',
    height: '100%',
    backgroundColor: selectedColor.background,
    borderRadius: '6px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    overflow: 'hidden',
    transition: 'all 0.3s ease'
  };
  
  const contentStyle = {
    position: 'absolute',
    width: '100%',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '16px'
  };
  
  const emojiStyle = {
    fontSize: '2rem',
    marginBottom: '8px'
  };
  
  const titleStyle = {
    fontSize: '1.125rem',
    fontWeight: '600',
    textAlign: 'center'
  };
  
  const flapStyle = {
    position: 'absolute',
    width: '100%',
    height: '50%',
    backgroundColor: selectedColor.flap,
    clipPath: 'polygon(0 100%, 50% 0, 100% 100%)',
    transform: isHovered ? 'rotateX(-180deg)' : 'rotateX(0deg)',
    transformOrigin: 'top',
    transition: 'transform 0.5s ease-in-out',
    zIndex: 10
  };
  
  const sealStyle = {
    position: 'absolute',
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    backgroundColor: selectedColor.seal,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    left: 'calc(50% - 20px)',
    top: 'calc(50% - 20px)',
    zIndex: isHovered ? 0 : 20,
    opacity: isHovered ? 0 : 1,
    transition: 'opacity 0.3s ease-in-out'
  };
  
  const heartStyle = {
    color: '#fff',
    fontSize: '1.25rem'
  };
  
  return (
    <div 
      style={containerStyle}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onClick}
    >
      {/* Envelope Body */}
      <div style={envelopeStyle}>
        {/* Envelope Content */}
        <div style={contentStyle}>
          <div style={emojiStyle}>{emoji}</div>
          <div style={titleStyle}>{title}</div>
        </div>
      </div>
      
      {/* Envelope Flap */}
      <div style={flapStyle} />
      
      {/* Envelope Seal (Heart) */}
      <div style={sealStyle}>
        <div style={heartStyle}>❤️</div>
      </div>
    </div>
  );
};

export default EnvelopeCard;
