import React from 'react';

const WishesModal = ({ onClose }) => {
  // Sample data - in a real app, this would come from your state or database
  const wishes = [
    { 
      id: 1, 
      title: "Happy Birthday", 
      message: "Wishing you health, happiness, and all the joy your heart can hold!" 
    },
    { 
      id: 2, 
      title: "Special Day", 
      message: "May this special day bring you endless happiness and lots of wonderful surprises!" 
    },
    { 
      id: 3, 
      title: "Future Success", 
      message: "May all your dreams and wishes come true in this upcoming year!" 
    }
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-8 max-w-2xl w-full max-h-[80vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-yellow-700">Special Wishes</h2>
          <button 
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700 text-2xl"
          >
            &times;
          </button>
        </div>
        
        <div className="space-y-6">
          {wishes.map(wish => (
            <div 
              key={wish.id} 
              className="bg-yellow-50 p-6 rounded-lg border-l-4 border-yellow-500"
            >
              <div className="flex items-center mb-2">
                <span className="text-xl mr-2">âœ¨</span>
                <h3 className="text-yellow-800 font-semibold text-lg">{wish.title}</h3>
              </div>
              <p className="mt-2 text-gray-700">{wish.message}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default WishesModal;
