import React from 'react';

const PhotosModal = ({ onClose }) => {
  // Sample data - in a real app, this would come from your state or database
  const photos = [
    { 
      id: 1, 
      title: "Beach Day", 
      url: "/api/placeholder/400/300", // Replace with your actual image path
      caption: "That amazing day at the beach last summer!" 
    },
    { 
      id: 2, 
      title: "Birthday Party", 
      url: "/api/placeholder/400/300", // Replace with your actual image path
      caption: "Your surprise party last year!" 
    },
    { 
      id: 3, 
      title: "Road Trip", 
      url: "/api/placeholder/400/300", // Replace with your actual image path
      caption: "Our adventure through the mountains" 
    }
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-8 max-w-4xl w-full max-h-[80vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-blue-700">Photo Memories</h2>
          <button 
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700 text-2xl"
          >
            &times;
          </button>
        </div>
        
        <div className="grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {photos.map(photo => (
            <div 
              key={photo.id} 
              className="bg-blue-50 rounded-lg overflow-hidden shadow-md"
            >
              <img 
                src={photo.url} 
                alt={photo.title}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h3 className="text-blue-800 font-semibold text-lg">{photo.title}</h3>
                <p className="mt-2 text-gray-700">{photo.caption}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PhotosModal;
