import { useEffect, useRef, useState } from "react";
import "../GiftBox.css";

const GiftBox = () => {
  const canvasRef = useRef(null);
  const [isOpened, setIsOpened] = useState(false);

  useEffect(() => {
    if (!isOpened) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const w = (canvas.width = 300);
    const h = (canvas.height = 200);
    let hw = w / 2;
    let hh = h / 2;

    const opts = {
      strings: ["HAPPY", "BIRTHDAY!"],
      charSize: 16,
      charSpacing: 20,
      lineHeight: 25,
      fireworkPrevPoints: 6,
      fireworkBaseLineWidth: 2,
      fireworkAddedLineWidth: 3,
      fireworkSpawnTime: 200,
      fireworkBaseReachTime: 30,
      fireworkAddedReachTime: 30,
      gravity: 0.08,
      upFlow: -0.1,
      letterContemplatingWaitTime: 360,
      balloonBaseInflateTime: 50,
      balloonAddedInflateTime: 30,
      balloonBaseVel: 0.3,
      balloonAddedVel: 0.2,
      balloonBaseRadian: -(Math.PI / 2 - 0.5),
      balloonAddedRadian: -1,
    };

    const Tau = Math.PI * 2;
    const TauQuarter = Tau / 4;

    const letters = [];

    function Letter(char, x, y) {
      this.char = char;
      this.finalX = x;
      this.finalY = y;
      this.x = 0;
      this.y = hh;
      this.phase = "firework";
      this.tick = 0;
      this.spawned = false;
      this.spawningTime = (opts.fireworkSpawnTime * Math.random()) | 0;
      this.reachTime = (opts.fireworkBaseReachTime + opts.fireworkAddedReachTime * Math.random()) | 0;
      this.lineWidth = opts.fireworkBaseLineWidth + opts.fireworkAddedLineWidth * Math.random();
      this.prevPoints = [[0, hh, 0]];
      const hue = (x / (opts.charSpacing * opts.strings[0].length)) * 360;
      this.color = `hsl(${hue},80%,60%)`;
      this.inflation = 0;
      this.inflateTime = (opts.balloonBaseInflateTime + opts.balloonAddedInflateTime * Math.random()) | 0;
      this.floatVX = (Math.random() - 0.5) * 0.5;
      this.floatVY = -(opts.balloonBaseVel + opts.balloonAddedVel * Math.random());
    }

    Letter.prototype.step = function () {
      if (this.phase === "firework") {
        if (!this.spawned) {
          this.tick++;
          if (this.tick >= this.spawningTime) {
            this.tick = 0;
            this.spawned = true;
          }
        } else {
          this.tick++;
          const progress = this.tick / this.reachTime;
          const armonicProgress = Math.sin(progress * TauQuarter);

          this.x = progress * this.finalX;
          this.y = hh + armonicProgress * (this.finalY - hh);

          if (this.tick >= this.reachTime) {
            this.phase = "contemplate";
            this.wait = opts.letterContemplatingWaitTime;
          }
        }
      } else if (this.phase === "contemplate") {
        ctx.fillStyle = this.color;
        ctx.font = `${opts.charSize}px Verdana`;
        ctx.fillText(this.char, this.x - ctx.measureText(this.char).width/2, this.y + opts.charSize/2);

        this.wait--;
        if (this.wait <= 0) {
          this.phase = "inflate";
          this.tick = 0;
        }
      } else if (this.phase === "inflate") {
        this.inflation = Math.min(1, this.tick / this.inflateTime);

        drawBalloon(ctx, this.x, this.y - 30 * this.inflation, this.inflation * 15, this.color);
        ctx.fillStyle = "white";
        ctx.font = `${opts.charSize}px Verdana`;
        ctx.fillText(this.char, this.x - ctx.measureText(this.char).width/2, this.y + opts.charSize/2);

        this.tick++;
        if (this.inflation >= 1) {
          this.phase = "float";
        }
      } else if (this.phase === "float") {
        this.x += this.floatVX;
        this.y += this.floatVY;

        drawBalloon(ctx, this.x, this.y - 30, 15, this.color);
        ctx.fillStyle = "white";
        ctx.font = `${opts.charSize}px Verdana`;
        ctx.fillText(this.char, this.x - ctx.measureText(this.char).width/2, this.y + opts.charSize/2);
      }
    };

    function drawBalloon(ctx, x, y, size, color) {
      ctx.beginPath();
      ctx.moveTo(x, y);
      ctx.bezierCurveTo(x - size, y - size * 2, x + size, y - size * 2, x, y);
      ctx.fillStyle = color;
      ctx.fill();
      ctx.beginPath();
      ctx.moveTo(x, y);
      ctx.lineTo(x, y + size * 1.5);
      ctx.strokeStyle = color;
      ctx.stroke();
    }

    function init() {
      letters.length = 0;
      ctx.font = `${opts.charSize}px Verdana`;
      for (let i = 0; i < opts.strings.length; ++i) {
        for (let j = 0; j < opts.strings[i].length; ++j) {
          letters.push(new Letter(
            j * opts.charSpacing + opts.charSpacing/2 - (opts.strings[i].length * opts.charSize) / 2,
            i * opts.lineHeight + opts.lineHeight/2 - (opts.strings.length * opts.lineHeight) / 2
          , opts.strings[i][j]));
        }
      }
      requestAnimationFrame(anim);
    }

    function anim() {
      ctx.clearRect(0, 0, w, h);
      ctx.save();
      ctx.translate(hw, hh);
      for (let l of letters) l.step();
      ctx.restore();
      requestAnimationFrame(anim);
    }

    init();
  }, [isOpened]);

  return (
    <div className="giftbox-wrapper">
      <div className="giftcontainer">
        <div className="gift">
          <input
            type="checkbox"
            id="gift-click"
            checked={isOpened}
            onChange={(e) => setIsOpened(e.target.checked)}
          />
          <label htmlFor="gift-click" className="click"></label>
        </div>
      </div>
      <div className={`animation-section ${isOpened ? "visible" : "hidden"}`}>
        <canvas ref={canvasRef}></canvas>
      </div>
    </div>
  );
};

export default GiftBox;
