import React, { useState } from 'react';
import '../Envelope.css';

const AddContentButton = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [contentType, setContentType] = useState('message');
  
  const openModal = () => {
    setIsModalOpen(true);
  };
  
  const closeModal = () => {
    setIsModalOpen(false);
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle submission logic here
    alert(`New ${contentType} added!`);
    closeModal();
  };
  
  return (
    <>
      <button className="add-button" onClick={openModal}>
        <svg 
          xmlns="http://www.w3.org/2000/svg" 
          width="20" 
          height="20" 
          viewBox="0 0 24 24" 
          fill="none" 
          stroke="currentColor" 
          strokeWidth="2" 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          style={{ marginRight: '8px' }}
        >
          <line x1="12" y1="5" x2="12" y2="19"></line>
          <line x1="5" y1="12" x2="19" y2="12"></line>
        </svg>
        Add New Content
      </button>
      
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2 className="modal-title">Add New Content</h2>
              <button className="modal-close" onClick={closeModal}>&times;</button>
            </div>
            
            <form onSubmit={handleSubmit}>
              {/* Simple form with buttons to select content type */}
              <div style={{ marginBottom: '16px', display: 'flex', borderBottom: '1px solid #e5e7eb' }}>
                <button 
                  type="button"
                  style={{ 
                    padding: '8px 16px', 
                    borderBottom: contentType === 'message' ? '2px solid #ec4899' : 'none',
                    color: contentType === 'message' ? '#ec4899' : '#6b7280',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer'
                  }}
                  onClick={() => setContentType('message')}
                >
                  Message
                </button>
                <button 
                  type="button"
                  style={{ 
                    padding: '8px 16px', 
                    borderBottom: contentType === 'photo' ? '2px solid #ec4899' : 'none',
                    color: contentType === 'photo' ? '#ec4899' : '#6b7280',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer'
                  }}
                  onClick={() => setContentType('photo')}
                >
                  Photo
                </button>
                <button 
                  type="button"
                  style={{ 
                    padding: '8px 16px', 
                    borderBottom: contentType === 'wish' ? '2px solid #ec4899' : 'none',
                    color: contentType === 'wish' ? '#ec4899' : '#6b7280',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer'
                  }}
                  onClick={() => setContentType('wish')}
                >
                  Wish
                </button>
              </div>
              
              {/* Form fields would go here */}
              <div style={{ marginTop: '24px', display: 'flex', justifyContent: 'flex-end' }}>
                <button
                  type="button"
                  onClick={closeModal}
                  style={{ 
                    padding: '8px 16px', 
                    marginRight: '8px',
                    color: '#6b7280',
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer'
                  }}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  style={{ 
                    padding: '8px 16px', 
                    backgroundColor: '#ec4899', 
                    color: 'white', 
                    borderRadius: '6px',
                    border: 'none',
                    cursor: 'pointer'
                  }}
                >
                  Add Content
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default AddContentButton;
