import React, { useState } from 'react';
import Modal from './Modal';

function Envelopes() {
  const [modalType, setModalType] = useState(null);

  const handleOpen = (type) => {
    setModalType(type);
  };

  const handleClose = () => {
    setModalType(null);
  };

  return (
    <section className="envelopes-section">
      <div className="container">
        <h2 className="section-title">Special Messages</h2>
        
        <div className="envelopes">
          <div className="envelope envelope--yellow" onClick={() => handleOpen('wishes')}>
            <div className="envelope-flap"></div>
            <div className="envelope-content">
              <span>Wishes</span>
            </div>
          </div>

          <div className="envelope envelope--blue" onClick={() => handleOpen('photos')}>
            <div className="envelope-flap"></div>
            <div className="envelope-content">
              <span>Photos</span>
            </div>
          </div>

          <div className="envelope envelope--pink" onClick={() => handleOpen('surprises')}>
            <div className="envelope-flap"></div>
            <div className="envelope-content">
              <span>Surprise</span>
            </div>
          </div>
        </div>

        {/* Add a new content button if you want later */}
      </div>

      {/* Modals */}
      <Modal open={modalType === 'wishes'} onClose={handleClose}>
        <h2>Birthday Wishes üéÇ</h2>
        <p>Happy Birthday Zizi! You are amazing!</p>
      </Modal>

      <Modal open={modalType === 'photos'} onClose={handleClose}>
        <h2>Photo Memories üì∑</h2>
        <p>Photos coming soon!</p>
      </Modal>

      <Modal open={modalType === 'surprises'} onClose={handleClose}>
        <h2>Special Surprises üéÅ</h2>
        <p>Surprise surprise!</p>
      </Modal>
    </section>
  );
}

export default Envelopes;
