import React, { useState } from 'react';

const AddContentButton = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [contentType, setContentType] = useState('message'); // 'message', 'photo', or 'wish'
  
  const openModal = () => {
    setIsModalOpen(true);
  };
  
  const closeModal = () => {
    setIsModalOpen(false);
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would handle the form submission
    alert(`New ${contentType} added!`);
    closeModal();
  };
  
  // Button styles
  const buttonStyle = {
    backgroundColor: '#ec4899',
    color: 'white',
    fontWeight: 'bold',
    padding: '12px 24px',
    borderRadius: '9999px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    alignItems: 'center',
    transition: 'all 0.3s ease',
    cursor: 'pointer',
    border: 'none'
  };
  
  const buttonHoverStyle = {
    backgroundColor: '#db2777',
    transform: 'scale(1.05)'
  };
  
  // Modal styles
  const modalOverlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 50
  };
  
  const modalContentStyle = {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '32px',
    maxWidth: '500px',
    width: '100%'
  };
  
  return (
    <>
      <button
        onClick={openModal}
        style={buttonStyle}
        onMouseOver={(e) => {
          Object.assign(e.currentTarget.style, buttonHoverStyle);
        }}
        onMouseOut={(e) => {
          e.currentTarget.style.backgroundColor = buttonStyle.backgroundColor;
          e.currentTarget.style.transform = 'scale(1)';
        }}
      >
        <svg 
          xmlns="http://www.w3.org/2000/svg" 
          width="20" 
          height="20" 
          viewBox="0 0 24 24" 
          fill="none" 
          stroke="currentColor" 
          strokeWidth="2" 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          style={{ marginRight: '8px' }}
        >
          <line x1="12" y1="5" x2="12" y2="19"></line>
          <line x1="5" y1="12" x2="19" y2="12"></line>
        </svg>
        Add New Content
      </button>
      
      {/* Add Content Modal */}
      {isModalOpen && (
        <div style={modalOverlayStyle}>
          <div style={modalContentStyle}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: 'bold' }}>Add New Content</h2>
              <button 
                onClick={closeModal}
                style={{ fontSize: '24px', color: '#6b7280', background: 'none', border: 'none', cursor: 'pointer' }}
              >
                &times;
              </button>
            </div>
            
            {/* Simple form would go here */}
            <form onSubmit={handleSubmit}>
              {/* Form fields would go here */}
              <div style={{ marginTop: '24px', display: 'flex', justifyContent: 'flex-end' }}>
                <button
                  type="button"
                  onClick={closeModal}
                  style={{ padding: '8px 16px', color: '#6b7280', marginRight: '8px', background: 'none', border: 'none', cursor: 'pointer' }}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  style={{ padding: '8px 16px', backgroundColor: '#ec4899', color: 'white', borderRadius: '6px', border: 'none', cursor: 'pointer' }}
                >
                  Add Content
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default AddContentButton;
