import React, { useState } from 'react';
import Modal from './Modal';

function Card() {
  const [open, setOpen] = useState(false);

  const handleCardClick = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div className="card-container">
      <div className="card" id="birthday-card" onClick={handleCardClick}>
        <div className="card-front">
          <p>From Bleuzz to Zizi</p>
        </div>
        <div className="card-inside">
          <p>Wishing you a very happy birthday filled with love and laughter!</p>
        </div>
      </div>

      <Modal open={open} onClose={handleClose}>
        <div className="card-message">
          <h2>Dear Zizi,</h2>
          <p>On this special day, I want to take a moment to celebrate you and all the joy you bring to my life.</p>
          <p>Happy Birthday, my dear friend!</p>
          <p className="signature">With love, <br /> Bleuzz</p>
        </div>
      </Modal>
    </div>
  );
}

export default Card;
