import React, { useState } from 'react';

function GiftBox() {
  const [open, setOpen] = useState(false);

  const handleGiftClick = () => {
    setOpen(!open);
  };

  return (
    <div className="gift-container">
      <div className={`gift-box ${open ? 'open' : ''}`} onClick={handleGiftClick}>
        <div className="gift-lid"></div>
        <div className="gift-body"></div>
        <div className="gift-ribbon"></div>
        <div className="gift-bow">
          <div className="gift-bow-left"></div>
          <div className="gift-bow-right"></div>
          <div className="gift-bow-center"></div>
        </div>
      </div>
    </div>
  );
}

export default GiftBox;
