import React from 'react';
import '../Envelope.css';

const WishesModal = ({ onClose }) => {
  // Sample data - in a real app, this would come from props or state
  const wishes = [
    { 
      id: 1, 
      title: "Happy Birthday", 
      message: "Wishing you health, happiness, and all the joy your heart can hold!" 
    },
    { 
      id: 2, 
      title: "Special Day", 
      message: "May this special day bring you endless happiness and lots of wonderful surprises!" 
    },
    { 
      id: 3, 
      title: "Future Success", 
      message: "May all your dreams and wishes come true in this upcoming year!" 
    }
  ];

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2 className="modal-title" style={{ color: '#fbc02d' }}>Special Wishes</h2>
          <button className="modal-close" onClick={onClose}>&times;</button>
        </div>
        
        <div>
          {wishes.map(wish => (
            <div key={wish.id} className="modal-item yellow-theme">
              <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
                <span style={{ fontSize: '20px', marginRight: '8px' }}>âœ¨</span>
                <h3 className="modal-item-title" style={{ color: '#fbc02d', margin: 0 }}>{wish.title}</h3>
              </div>
              <p className="modal-item-content">{wish.message}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default WishesModal;
