import React, { useState } from 'react';
import Header from './components/Header';
import Balloons from './components/Balloons';
import MainContent from './components/MainContent';
import Flower from './components/Flower';
import EnvelopeCard from './components/EnvelopeCard';
import FriendsModal from './components/FriendsModal';
import PhotosModal from './components/PhotosModal';
import WishesModal from './components/WishesModal';
import AddContentButton from './components/AddContentButton';

function App() {
  const [activeModal, setActiveModal] = useState(null);

  const openModal = (modalType) => {
    setActiveModal(modalType);
  };

  const closeModal = () => {
    setActiveModal(null);
  };

  // Set explicit background style to ensure it's not white
  const envelopeSectionStyle = {
    backgroundColor: '#f0f0f0', // Light gray background
    padding: '32px 0',
    margin: '20px 0'
  };

  const containerStyle = {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 16px'
  };

  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '24px'
  };

  const addButtonContainerStyle = {
    marginTop: '48px',
    display: 'flex',
    justifyContent: 'center'
  };

  return (
    <div>
      <Header />
      <Balloons />

      {/* Envelope Section with explicit styles */}
      <div style={envelopeSectionStyle}>
        <div style={containerStyle}>
          <div style={gridStyle}>
            {/* First Letter - Friends Messages */}
            <EnvelopeCard
              color="purple"
              emoji="ðŸ’Œ"
              title="Messages from Friends"
              onClick={() => openModal('friends')}
            />

            {/* Second Letter - Photo Memories */}
            <EnvelopeCard
              color="blue"
              emoji="ðŸ“¸"
              title="Photo Memories"
              onClick={() => openModal('photos')}
            />

            {/* Third Letter - Special Wishes */}
            <EnvelopeCard
              color="yellow"
              emoji="âœ¨"
              title="Special Wishes"
              onClick={() => openModal('wishes')}
            />
          </div>

          {/* Add New Content Button */}
          <div style={addButtonContainerStyle}>
            <AddContentButton />
          </div>
        </div>
      </div>

      <MainContent />
      <Flower />

      {/* Modals */}
      {activeModal === 'friends' && (
        <FriendsModal onClose={closeModal} />
      )}

      {activeModal === 'photos' && (
        <PhotosModal onClose={closeModal} />
      )}

      {activeModal === 'wishes' && (
        <WishesModal onClose={closeModal} />
      )}
    </div>
  );
}

export default App;
