import React from 'react';
import '../Envelope.css';

const PhotosModal = ({ onClose }) => {
  // Sample data - in a real app, this would come from props or state
  const photos = [
    { 
      id: 1, 
      title: "Beach Day", 
      url: "https://via.placeholder.com/300", // Replace with actual image path
      caption: "That amazing day at the beach last summer!" 
    },
    { 
      id: 2, 
      title: "Birthday Party", 
      url: "https://via.placeholder.com/300", // Replace with actual image path
      caption: "Your surprise party last year!" 
    },
    { 
      id: 3, 
      title: "Road Trip", 
      url: "https://via.placeholder.com/300", // Replace with actual image path
      caption: "Our adventure through the mountains" 
    }
  ];

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2 className="modal-title" style={{ color: '#1976d2' }}>Photo Memories</h2>
          <button className="modal-close" onClick={onClose}>&times;</button>
        </div>
        
        <div>
          {photos.map(photo => (
            <div key={photo.id} className="modal-item blue-theme" style={{ marginBottom: '24px' }}>
              <h3 className="modal-item-title" style={{ color: '#1976d2' }}>{photo.title}</h3>
              <img 
                src={photo.url} 
                alt={photo.title}
                style={{ width: '100%', height: 'auto', borderRadius: '4px', marginBottom: '8px' }}
              />
              <p className="modal-item-content">{photo.caption}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PhotosModal;
