import { useState, useEffect } from "react";
import "../Zizi.css";

export default function FriendshipPage({
  title = "For My Amazing Friend",
  subtitle = "A collection of love and memories just for you",
  initialMessages = [
    {
      author: "Sarah",
      text: "You've been such an amazing friend to me all these years. Thank you for always being there when I needed someone to talk to. You make the world a better place just by being you!",
    },
    {
      author: "Michael",
      text: "Remember when we stayed up all night talking about our dreams? Those are the moments I treasure the most. You inspire me to be a better person every day!",
    },
    {
      author: "Emma",
      text: "Your smile brightens even the darkest days. Never stop being the wonderful, caring person that you are. You deserve all the happiness in the world!",
    },
  ],
  initialPhotos = [
    { src: process.env.PUBLIC_URL + "/images/blz.jpg", caption: "At the beach" },
    { src: process.env.PUBLIC_URL + "/images/amina.jpg", caption: "At the beach" },
    { src: process.env.PUBLIC_URL + "/images/blz.jpg", caption: "Birthday celebration last year" },
  ],
  welcomeText = "Welcome to Your Special Page!",
  specialNote = "In a world full of ordinary, you are extraordinarily special. Your kindness knows no bounds, your laughter is contagious, and your friendship is a treasure I hold dear. Through thick and thin, ups and downs, you've been there - a constant star in my sky. This page is just a small token of my appreciation for all that you are and all that you do. May your days be filled with as much joy as you bring to others!",
  cardLabels = {
    messages: "me and zizi",
    photos: "Photo memories",
    surprises: "Special surprises"
  },
  showAdminControls = true,
  welcomeDuration = 3000,
  balloonCount = 20,
  confettiCount = 50
}) {
  const [showWelcome, setShowWelcome] = useState(true);
  const [activeModal, setActiveModal] = useState(null);
  const [activeTab, setActiveTab] = useState("messageTab");
  const [messages, setMessages] = useState(initialMessages);
  const [photos, setPhotos] = useState(initialPhotos);

  // Form states
  const [messageName, setMessageName] = useState("");
  const [messageText, setMessageText] = useState("");
  const [photoCaption, setPhotoCaption] = useState("");
  const [surpriseTitle, setSurpriseTitle] = useState("");
  const [surpriseContent, setSurpriseContent] = useState("");

  useEffect(() => {
    // Hide welcome animation after specified duration
    const timer = setTimeout(() => {
      setShowWelcome(false);
    }, welcomeDuration);

    // Create balloons
    for (let i = 0; i < balloonCount; i++) {
      const balloon = document.createElement("div");
      balloon.className = "balloon";
      balloon.style.left = Math.random() * 100 + "vw";
      balloon.style.animationDelay = Math.random() * 5 + "s";
      document.body.appendChild(balloon);
    }

    // Create confetti for the welcome animation
    for (let i = 0; i < confettiCount; i++) {
      setTimeout(() => {
        if (document.querySelector(".welcome-animation")) {
          const confetti = document.createElement("div");
          confetti.className = "confetti";
          confetti.style.left = Math.random() * 100 + "vw";
          confetti.style.animationDelay = Math.random() * 2 + "s";
          document.querySelector(".welcome-animation").appendChild(confetti);
        }
      }, i * 100);
    }

    return () => {
      clearTimeout(timer);
      // Clean up balloons when component unmounts
      const balloons = document.querySelectorAll(".balloon");
      balloons.forEach(balloon => balloon.remove());
    };
  }, [welcomeDuration, balloonCount, confettiCount]);

  const handleMessageSubmit = (e) => {
    e.preventDefault();
    if (messageName && messageText) {
      setMessages([{ author: messageName, text: messageText }, ...messages]);
      setMessageName("");
      setMessageText("");
      setTimeout(() => {
        setActiveModal(null);
      }, 500);
    }
  };

  const handlePhotoSubmit = (e) => {
    e.preventDefault();
    if (photoCaption) {
      setPhotos([
        { src: "/api/placeholder/400/320", caption: photoCaption },
        ...photos,
      ]);
      setPhotoCaption("");
      setTimeout(() => {
        setActiveModal(null);
      }, 500);
    }
  };

  const handleSurpriseSubmit = (e) => {
    e.preventDefault();
    alert("Surprise content added! It will appear in the surprises section.");
    setSurpriseTitle("");
    setSurpriseContent("");
    setTimeout(() => {
      setActiveModal(null);
    }, 500);
  };

  return (
    <div className="app-container">
      {/* Welcome Animation */}
      {showWelcome && (
        <div className="welcome-animation">
          <div className="cake">
            <div className="cake-base"></div>
            <div className="cake-middle"></div>
            <div className="cake-top"></div>
            <div className="candle"></div>
            <div className="flame"></div>
          </div>
          <h2 className="welcome-text">{welcomeText}</h2>
        </div>
      )}

      {/* Main Content */}
      <div className="container">
        <header>
          <h1>{title}</h1>
          <p className="subtitle">{subtitle}</p>
        </header>

        <div className="letter-container">
          <EnvelopeCard
            emoji="❤️"
            text={cardLabels.messages}
            colorClass="color-purple"
            onClick={() => setActiveModal("messageModal")}
          />
          <EnvelopeCard
            emoji="📸"
            text={cardLabels.photos}
            colorClass="color-blue"
            onClick={() => setActiveModal("photoModal")}
          />
          <EnvelopeCard
            emoji="🎁"
            text={cardLabels.surprises}
            colorClass="color-pink"
            onClick={() => setActiveModal("surpriseModal")}
          />
        </div>

        {showAdminControls && (
          <div className="admin-controls">
            <button
              className="add-button"
              onClick={() => setActiveModal("adminModal")}
            >
              Add New Content
            </button>
          </div>
        )}
      </div>

      {/* Message Modal */}
      <Modal
        isOpen={activeModal === "messageModal"}
        onClose={() => setActiveModal(null)}
        title="Messages From Friends"
      >
        <div className="messages-container">
          {messages.map((message, index) => (
            <div key={index} className="message">
              <div className="message-author">{message.author}</div>
              <div className="message-text">{message.text}</div>
            </div>
          ))}
        </div>
      </Modal>

      {/* Photo Modal */}
      <Modal
        isOpen={activeModal === "photoModal"}
        onClose={() => setActiveModal(null)}
        title="Photo Memories"
      >
        <div className="photos-container">
          {photos.map((photo, index) => (
            <div key={index} className="photo-item">
              <img src={photo.src} alt="Memory" />
              <div className="photo-caption">{photo.caption}</div>
            </div>
          ))}
        </div>
      </Modal>

      {/* Surprise Modal */}
      <Modal
        isOpen={activeModal === "surpriseModal"}
        onClose={() => setActiveModal(null)}
        title="Special Surprises"
      >
        <div className="surprise-content">
          <h3>A Special Note Just For You</h3>
          <p>{specialNote}</p>
          <div className="surprise-emoji">
            <div className="emoji">🎉</div>
            <p>More surprises coming soon...</p>
          </div>
        </div>
      </Modal>

      {/* Admin Modal */}
      <Modal
        isOpen={activeModal === "adminModal"}
        onClose={() => setActiveModal(null)}
        title="Add New Content"
      >
        <div className="nav-tabs">
          <div
            className={`nav-tab ${activeTab === "messageTab" ? "active" : ""}`}
            onClick={() => setActiveTab("messageTab")}
          >
            Add Message
          </div>
          <div
            className={`nav-tab ${activeTab === "photoTab" ? "active" : ""}`}
            onClick={() => setActiveTab("photoTab")}
          >
            Add Photo
          </div>
          <div
            className={`nav-tab ${activeTab === "surpriseTab" ? "active" : ""}`}
            onClick={() => setActiveTab("surpriseTab")}
          >
            Add Surprise
          </div>
        </div>

        {activeTab === "messageTab" && (
          <form className="add-form" onSubmit={handleMessageSubmit}>
            <div className="form-group">
              <label htmlFor="messageName">Name</label>
              <input
                type="text"
                id="messageName"
                value={messageName}
                onChange={(e) => setMessageName(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="messageText">Your Message</label>
              <textarea
                id="messageText"
                value={messageText}
                onChange={(e) => setMessageText(e.target.value)}
                required
              ></textarea>
            </div>
            <button type="submit" className="submit-btn">Add Message</button>
          </form>
        )}

        {activeTab === "photoTab" && (
          <form className="add-form" onSubmit={handlePhotoSubmit}>
            <div className="form-group">
              <label htmlFor="photoUpload">Upload Photo</label>
              <input
                type="file"
                id="photoUpload"
                accept="image/*"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="photoCaption">Caption</label>
              <input
                type="text"
                id="photoCaption"
                value={photoCaption}
                onChange={(e) => setPhotoCaption(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="submit-btn">Add Photo</button>
          </form>
        )}

        {activeTab === "surpriseTab" && (
          <form className="add-form" onSubmit={handleSurpriseSubmit}>
            <div className="form-group">
              <label htmlFor="surpriseTitle">Surprise Title</label>
              <input
                type="text"
                id="surpriseTitle"
                value={surpriseTitle}
                onChange={(e) => setSurpriseTitle(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="surpriseContent">Surprise Content</label>
              <textarea
                id="surpriseContent"
                value={surpriseContent}
                onChange={(e) => setSurpriseContent(e.target.value)}
                required
              ></textarea>
            </div>
            <button type="submit" className="submit-btn">Add Surprise</button>
          </form>
        )}
      </Modal>
    </div>
  );
}

// EnvelopeCard Component
function EnvelopeCard({ emoji, text, colorClass, onClick }) {
  return (
    <div className="envelope" onClick={onClick}>
      <div className={`envelope-flap ${colorClass}`}></div>
      <div className="envelope-content">
        <span>{emoji}</span>
        <p className={colorClass}>{text}</p>
      </div>
    </div>
  );
}

// Modal Component
function Modal({ isOpen, onClose, title, children }) {
  if (!isOpen) return null;

  return (
    <div className="modal" onClick={(e) => {
      if (e.target === e.currentTarget) onClose();
    }}>
      <div className="modal-content">
        <button className="close-modal" onClick={onClose}>×</button>
        <h2 className="modal-title">{title}</h2>
        {children}
      </div>
    </div>
  );
}
