import React, { useState } from 'react';
import '../Envelope.css'; // Import the CSS file

const EnvelopeCard = ({ 
  color, 
  emoji, 
  title, 
  onClick 
}) => {
  const [isHovered, setIsHovered] = useState(false);
  
  // Define color styles based on the color prop
  const colorStyles = {
    purple: {
      background: '#e0c3fc',
      flap: '#d3a4fb',
      seal: '#9c27b0'
    },
    blue: {
      background: '#bbdefb',
      flap: '#90caf9',
      seal: '#1976d2'
    },
    yellow: {
      background: '#fff9c4',
      flap: '#fff59d',
      seal: '#fbc02d'
    }
  };
  
  // Get the selected color or default to purple
  const selectedColor = colorStyles[color] || colorStyles.purple;
  
  return (
    <div 
      className="envelope-container"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onClick}
    >
      {/* Envelope Body */}
      <div 
        className="envelope-body"
        style={{ backgroundColor: selectedColor.background }}
      >
        {/* Envelope Content */}
        <div className="envelope-content">
          <div className="envelope-emoji">{emoji}</div>
          <div className="envelope-title">{title}</div>
        </div>
      </div>
      
      {/* Envelope Flap */}
      <div 
        className={`envelope-flap ${isHovered ? 'open' : ''}`}
        style={{ backgroundColor: selectedColor.flap }}
      />
      
      {/* Envelope Seal (Heart) */}
      <div 
        className={`envelope-seal ${isHovered ? 'hidden' : ''}`}
        style={{ backgroundColor: selectedColor.seal }}
      >
        <div className="envelope-heart">❤️</div>
      </div>
    </div>
  );
};

export default EnvelopeCard;
